import { useState } from 'react';
import t from './tabs.module.css'
import Races from '../races/races';
import Horses from '../horses/horses';
import Jockeys from '../jockeys/jockeys';

const Tabs = () => {
    const [activeTab, setActiveTab] = useState("races")
    const handleRaces = () => {
        setActiveTab("races");
    };
    const handleHorses = () => {
        setActiveTab("horses");
    };
    const handleJockeys = () => {
        setActiveTab("jockeys");
    };
    return (
        <div>
            <ul className={t.tabs}>
                <li className={activeTab === "races" ? "active" : ""}>Заезды</li>
                <li className={activeTab === "horses" ? "active" : ""}>Лошади</li>
                <li className={activeTab === "jockeys" ? "active" : ""}>Жокеи</li>
            </ul>
            <div className={t.outlet}>
                {activeTab === "races" ? <Races /> : activeTab === "horses" ? <Horses /> : <Jockeys />}
                
            </div>
        </div>
    )
}

export default Tabs
